uint16_t golomb_encode(volatile int16_t samples[], int8_t optimal, bool output){
    uint8_t result[MAX_RESULT_LEN];
    uint16_t counter = 0;

    //store parameter
    for(int b = 0; b < 4; b++){
        result[counter++] = optimal & (1 << (3 - b)) ? 1 : 0;
    }

    //truncated parameter
    int16_t k = optimal;
    int16_t u = (1 << (k+1)) - (1 << optimal);


    for(int i = 0; i < SAMPLE_COUNT; i++){
        int16_t number = encode_signed(samples[i]);

        int16_t q = number >> optimal;
        int16_t r = number & ((1 << optimal) - 1);

        //unary code q
        for(int j = 0; j < q; j++){
            result[counter++] = 1;
        }
        //delimit unary with zero
        result[counter++] = 0;

        //truncated
        if(r < u){
            for(int b = 1; b <= k; b++){
                result[counter++] = r & (1 << (k - b)) ? 1 : 0;
            }
        } else {
            for(int b = 1; b <= k + 1; b++){
                result[counter++] = r & (1 << (k - b)) ? 1 : 0;
            }
        }

    }

    if(output){
        print_uart0_int(counter);
        print_uart0("\n");

        for(int c = 0; c < counter; c++){
            print_uart0_int(result[c]);
        }

        print_uart0("\n");
    }

    return(counter);
}

void golomb_decode(volatile uint8_t bits[], int16_t len){
    int16_t result[SAMPLE_COUNT];
    int8_t divider = 0;
    int16_t counter = 0;
    int16_t bitCounter = 0;

    //parameter
    for(int b = 1; b <= 4; b++){
        divider |= bits[bitCounter++] ? (1 << (4 - b)) : 0;
    }

    //truncated parameter
    int16_t k = divider;
    int16_t u = (1 << (k+1)) - (1 << divider);

    uint16_t unaryCounter = 0;

    for(int b = 4; b < len; b++){
        if(bits[b]){
            unaryCounter++;
        } else {
            uint16_t q = unaryCounter << divider;
            unaryCounter = 0;

            //consume delimiting zero
            b++;

            uint16_t r = 0;
            for(int i = 1; i <= k; i++){
                r |= bits[b++] ? (1 << (k - i)) : 0;
            }
            
            if(r > u){
                r = (r << 1) | bits[b];
            } else {
                b--;
            }

            result[counter++] = decode_signed(q+r);
        }
    }

    for(int c = 0; c < counter; c++){
        print_uart0_int(result[c]);
    }

    print_uart0("\n");
}



int16_t encode_signed(int16_t val) {
    if (val < 0) {
        return ((-val) << 1) - 1;
    } else {
        return val << 1;
    }
}

int16_t decode_signed(int16_t val) {
    if (val & 1) {
        return -((val + 1) >> 1);
    } else {
        return val >> 1;
    }
}